local function normalize(name)
    if not name then return "" end
    return name:lower():gsub("%s+", "-"):gsub("[^%w%-]", "")
end

local function formatEggName(name)
    if not name then return "" end
    name = name:gsub("-", " ")
    return name:gsub("(%a)([%w]*)", function(first, rest)
        return first:upper() .. rest:lower()
    end)
end

