local function formatEggName(name)
    if not name then return "" end
    name = name:gsub("-", " ")
    return name:gsub("(%a)([%w]*)", function(first, rest)
        return first:upper() .. rest:lower()
    end)
end

local function normalize(name)
    return (name or ""):lower():gsub("%W", "")
end

local function parseLuck(luckText)
    if not luckText then return 0 end
    return tonumber(luckText:match("%d+")) or 0
end

local function shouldSendRift(riftData, luckText)
    local luck = parseLuck(luckText)
    if riftData.MinLuck then
        return luck >= riftData.MinLuck
    else
        return true
    end
end

local function parseTimer(timerText)
    if not timerText or timerText == "" then return 0 end
    timerText = timerText:lower():gsub("%s+", "")

    local h, m, s = timerText:match("^(%d+):(%d+):(%d+)$")
    if h and m and s then
        return tonumber(h) * 3600 + tonumber(m) * 60 + tonumber(s)
    end

    m, s = timerText:match("^(%d+):(%d+)$")
    if m and s then
        return tonumber(m) * 60 + tonumber(s)
    end

    if #timerText <= 4 then
        local seconds = tonumber(timerText:sub(-2)) or 0
        local minutes = tonumber(timerText:sub(1, #timerText-2)) or 0
        return minutes * 60 + seconds
    end

    local hours = tonumber(timerText:match("(%d+)h")) or 0
    local minutes = tonumber(timerText:match("(%d+)m")) or 0
    local seconds = tonumber(timerText:match("(%d+)s")) or 0
    if hours > 0 or minutes > 0 or seconds > 0 then
        return hours * 3600 + minutes * 60 + seconds
    end

    local fallback = tonumber(timerText)
    if fallback then return fallback end

    return 0
end

