local function toRiftEggName(eggName)
    local normalized = eggName:lower():gsub("%s+", "-")
    if not normalized:match("egg$") then
        normalized = normalized .. "-egg"
    end
    return normalized
end

local bounty = SecretBounty:Get()
local bountyEgg = toRiftEggName(bounty.Egg)

