                                    color = 0x00FF00,
                                    fields = {
                                        {name = "ðŸŒ Server Info", value = "Players Online: " .. playerCount .. "/" .. maxPlayers, inline = true},
                                        {name = "ðŸŽ² Rift Info", value = "Luck: " .. luckText .. "\nExpires: <t:" .. expireTimestamp .. ":R>\nHeight: " .. heightText, inline = true},
                                        {name = "ðŸ”— Quick Join", value = "[Click to Join Server](https://www.roblox.com/games/start?placeId=" .. game.PlaceId .. "&jobId=" .. jobId .. ")", inline = false}
                                    },
                                    timestamp = DateTime.now():ToIsoDate()
                                }}
                            }

                            -- Send webhook immediately
                            sendWebhook(riftData.Webhook, message)
                            print("Webhook sent for:", rift.Name)

                            -- Hop servers if needed
                            hopServers()
                            foundRift = true
                        end
                    end
                end
            end
        end

        if not foundRift and (tick() - lastCheck) >= IDLE_HOP_TIME then
            hopServers()
            lastCheck = tick()
        end

        task.wait(1)
    end
end)