                        {name = "ðŸŽ² Rift Info", value = "Luck: " .. (luckText or "N/A") .. "\nExpires: <t:" .. expireTimestamp .. ":R>\nHeight: " .. (heightText or "N/A"), inline = true},
                        {name = "ðŸ”— Quick Join", value = "[Click to Join Server](" .. redirectURL .. ")", inline = false}
                    },
