                                    }}
                                }

                                sendWebhook(riftData.Webhook, message)
                                print("Webhook sent for:", rift.Name, "| Expires:", expireTimestamp)

                                foundRift = true
                            end
                        end
                    end
                end
            end
        end

        if not foundRift and (tick() - lastCheck) >= IDLE_HOP_TIME then
            hopServers()
            lastCheck = tick()
        end

        if foundRift then
            hopServers() 
        end

        task.wait(1)
    end
end)