            ::continue_rift::
        end

        if #riftsToSend > 0 then
            hopServers()
        elseif (tick() - lastCheck) >= IDLE_HOP_TIME then
            hopServers()
            lastCheck = tick()
        end

        task.wait(1)
    end
end)