local HttpService = game:GetService("HttpService")
local TeleportService = game:GetService("TeleportService")
local Players = game:GetService("Players")
local workspace = game:GetService("Workspace")
local TextChatService = game:GetService("TextChatService")

local player = Players.LocalPlayer

local RIFTS = {
    {Name = "lunar-egg", Webhook = "https://discord.com/api/webhooks/1415712364007002143/p80C7QElE5O1EEDo-0IKJA5cGiG31O8qlEBQ1dgmibyOtO2Fr228CK7-JQiM2vpLb8Mz", MinLuck = 25},  
    {Name = "brainrot-egg", Webhook = "https://discord.com/api/webhooks/1415718364055077025/_cblNWmsQS35E-1xCz-CQWYMbiKm4aFncF_0ngpDsavEPFPbfL5QUE1nP7kmk2xWzy1V", MinLuck = nil}  
}

local HOP_COOLDOWN = 5
local IDLE_HOP_TIME = 5
local isHopping = false
local alreadyFound = {}
local lastCheck = tick()

-- Helpers
local function formatEggName(name)
    if not name then return "" end
    name = name:gsub("-", " ")
    return name:gsub("(%a)([%w]*)", function(first, rest)
        return first:upper() .. rest:lower()
    end)
end

local function normalize(name)
    return (name or ""):lower():gsub("%W", "")
end

local function parseLuck(luckText)
    if not luckText then return 0 end
    return tonumber(luckText:match("%d+")) or 0
end

local function shouldSendRift(riftData, luckText)
    local luck = parseLuck(luckText)
    if riftData.MinLuck then
        return luck >= riftData.MinLuck
    else
        return true
    end
end

-- Robust timer parsing function
local function parseTimer(timerText)
    if not timerText or timerText == "" then return nil end

    -- Try MM:SS first
    local mm, ss = timerText:match("(%d+):(%d+)")
    if mm and ss then
        return tonumber(mm) * 60 + tonumber(ss)
    end

    -- Try "X m Y s" format
    local m, s = timerText:match("(%d+)%s*m"), timerText:match("(%d+)%s*s")
    m = tonumber(m) or 0
    s = tonumber(s) or 0
    if m == 0 and s == 0 then return nil end

    return m * 60 + s
end

-- Webhook sender
local function sendWebhook(url, payload)
    local body = HttpService:JSONEncode(payload)
    local success, err = pcall(function()
        if syn and syn.request then
            syn.request({
                Url = url,
                Method = "POST",
                Headers = { ["Content-Type"] = "application/json" },
                Body = body
            })
        elseif request then
            request({
                Url = url,
                Method = "POST",
                Headers = { ["Content-Type"] = "application/json" },
                Body = body
            })
        else
            error("Cannot send webhook: no supported HTTP request function found")
        end
    end)
    if not success then
        warn("Webhook failed:", err)
    end
end

-- Server hopping
local function getServerArray()
    if type(SERVER_LIST) ~= "table" then return {} end
    return SERVER_LIST
end

TeleportService.TeleportInitFailed:Connect(function(player, result, errMsg)
    warn("Teleport failed:", result, errMsg)
    isHopping = false
end)

local function hopServers()
    if isHopping then return end
    local servers = getServerArray()
    if not servers or #servers == 0 then
        warn("hopServers: no servers available")
        return
    end

    local currentJob = tostring(game.JobId or "")
    local chosenJob = nil

    for _ = 1, #servers do
        local candidate = tostring(servers[math.random(1, #servers)] or "")
        if candidate ~= "" and candidate ~= currentJob then
            chosenJob = candidate
            break
        end
    end

    if not chosenJob then
        warn("No new server found (only current one).")
        return
    end

    isHopping = true
    warn("Teleporting to server:", chosenJob)
    pcall(function()
        TeleportService:TeleportToPlaceInstance(game.PlaceId, chosenJob, player)
    end)
    task.wait(HOP_COOLDOWN)
    isHopping = false
end

-- Chat spam
local function sendRiftChatSpam(times, delay, message)
    task.spawn(function()
        local channel
        repeat
            channel = next(TextChatService.TextChannels:GetChildren())
            task.wait(0.5)
        until channel

        for i = 1, times do
            pcall(function()
                local firstChannel = next(TextChatService.TextChannels:GetChildren())
                if firstChannel then
                    firstChannel:SendAsync(message)
                end
            end)
            task.wait(delay)
        end
        print("Chat messages sent!")
    end)
end

-- Main loop
task.spawn(function()
    task.wait(2)
    lastCheck = tick()

    while true do
        local foundRift = false
        local rendered = workspace:FindFirstChild("Rendered")
        if rendered then
            local riftsFolder = rendered:FindFirstChild("Rifts")
            if riftsFolder then
                for _, rift in ipairs(riftsFolder:GetChildren()) do
                    for _, riftData in ipairs(RIFTS) do
                        if riftData.Name and normalize(rift.Name):find(normalize(riftData.Name)) then

                            -- Rift details
                            local luckText, timerText, heightText = "N/A", "N/A", "N/A"

                            pcall(function()
                                if rift:FindFirstChild("Display") and rift.Display:FindFirstChild("SurfaceGui") then
                                    if rift.Display.SurfaceGui:FindFirstChild("Icon") and rift.Display.SurfaceGui.Icon:FindFirstChild("Luck") then
                                        luckText = rift.Display.SurfaceGui.Icon.Luck.Text or "N/A"
                                    end
                                    if rift.Display.SurfaceGui:FindFirstChild("Timer") then
                                        timerText = rift.Display.SurfaceGui.Timer.Text or ""
                                    end
                                end
                            end)

                            -- Parse timer safely
                            local secondsLeft = parseTimer(timerText)
                            if not secondsLeft or secondsLeft <= 0 then
                                continue -- skip expired rifts
                            end

                            local expireTimestamp = os.time() + secondsLeft

                            -- Rift height
                            pcall(function()
                                if rift.GetPivot then
                                    heightText = tostring(math.floor(rift:GetPivot().Position.Y)) .. " studs"
                                elseif rift:FindFirstChild("Display") and rift.Display:IsA("BasePart") then
                                    heightText = tostring(math.floor(rift.Display.Position.Y)) .. " studs"
                                end
                            end)

                            -- Skip if minimum luck not met
                            if not shouldSendRift(riftData, luckText) then
                                continue
                            end

                            -- Prepare webhook
                            local formattedEggName = formatEggName(rift.Name)
                            local jobId = tostring(game.JobId or "")
                            local playerCount = #Players:GetPlayers()
                            local maxPlayers = Players.MaxPlayers or 0

                            local message = {
                                embeds = {{
                                    title = formattedEggName .. " Has Been Found 🥚",
                                    color = 0x00FF00,
                                    fields = {
                                        {name = "🌍 Server Info", value = "Players Online: " .. playerCount .. "/" .. maxPlayers, inline = true},
                                        {name = "🎲 Rift Info", value = "Luck: " .. luckText .. "\nExpires: <t:" .. expireTimestamp .. ":R>\nHeight: " .. heightText, inline = true},
                                        {name = "🔗 Quick Join", value = "[Click to Join Server](https://www.roblox.com/games/start?placeId=" .. game.PlaceId .. "&jobId=" .. jobId .. ")", inline = false}
                                    },
                                    timestamp = DateTime.now():ToIsoDate()
                                }}
                            }

                            -- Send webhook immediately
                            sendWebhook(riftData.Webhook, message)
                            print("Webhook sent for:", rift.Name)

                            -- Hop servers if needed
                            hopServers()
                            foundRift = true
                        end
                    end
                end
            end
        end

        if not foundRift and (tick() - lastCheck) >= IDLE_HOP_TIME then
            hopServers()
            lastCheck = tick()
        end

        task.wait(1)
    end
end)