                    if not currentServerRifts[riftId] then
                        for _, riftData in ipairs(RIFTS) do
                            if riftData.Name and riftId:find(normalize(riftData.Name)) then
                                table.insert(riftsToSend, {rift = rift, riftData = riftData, riftId = riftId})
