                }}
            }

            sendWebhook(riftData.Webhook, message)
            print("Webhook sent for:", rift.Name, "| Expires in:", math.floor(secondsLeft / 60), "minutes", secondsLeft % 60, "seconds")
        end

        if #riftsToSend > 0 then
            hopServers()
        elseif (tick() - lastCheck) >= IDLE_HOP_TIME then
            hopServers()
            lastCheck = tick()
        end

        task.wait(1)
    end
end)
