local HOP_COOLDOWN = 15
local IDLE_HOP_TIME = 5
local isHopping = false
local lastCheck = tick()

local currentServerRifts = {}

local promoMessages = {
    "○●□ JOIN FOR RIFTS - q n 6 E R j S z ○●□",
    "○●□ FREE BOUNTY EGGS ○●□"
