local HttpService = game:GetService("HttpService")
local TeleportService = game:GetService("TeleportService")
local Players = game:GetService("Players")
local Workspace = game:GetService("Workspace")

local player = Players.LocalPlayer

-- Configuration
local RIFTS = {
    {Name = "lunar-egg", Webhook = "https://discord.com/api/webhooks/1415712364007002143/p80C7QElE5O1EEDo-0IKJA5cGiG31O8qlEBQ1dgmibyOtO2Fr228CK7-JQiM2vpLb8Mz", MinLuck = 25},
    {Name = "spikey-egg", Webhook = "https://discord.com/api/webhooks/1411629941178765365/k0Tw3CWiuZ935oPnQ4GnofN3xcWAc7q3KWg41_SwqtkOlyNotA5nNXzPjf1XVg1zcu4h", MinLuck = nil}
}

local HOP_COOLDOWN = 5
local IDLE_HOP_TIME = 5
local isHopping = false
local alreadyFound = {}
local lastCheck = tick()

-- Helpers
local function formatEggName(name)
    if not name then return "" end
    name = name:gsub("-", " ")
    return name:gsub("(%a)([%w]*)", function(first, rest)
        return first:upper() .. rest:lower()
    end)
end

local function normalize(name)
    return (name or ""):lower():gsub("%W", "")
end

local function parseLuck(luckText)
    if not luckText then return 0 end
    return tonumber(luckText:match("%d+")) or 0
end

local function shouldSendRift(riftData, luckText)
    local luck = parseLuck(luckText)
    return not riftData.MinLuck or luck >= riftData.MinLuck
end

-- Webhook sender
local function sendWebhook(url, payload)
    local body = HttpService:JSONEncode(payload)
    local success, err = pcall(function()
        if syn and syn.request then
            syn.request({Url = url, Method = "POST", Headers = {["Content-Type"] = "application/json"}, Body = body})
        elseif request then
            request({Url = url, Method = "POST", Headers = {["Content-Type"] = "application/json"}, Body = body})
        else
            error("Cannot send webhook: no supported HTTP request function found")
        end
    end)
    if not success then warn("Webhook failed:", err) end
end

-- Server hopping
local function getServerArray()
    return type(SERVER_LIST) == "table" and SERVER_LIST or {}
end

TeleportService.TeleportInitFailed:Connect(function(player, result, errMsg)
    warn("Teleport failed:", result, errMsg)
    isHopping = false
end)

local function hopServers()
    if isHopping then return end
    local servers = getServerArray()
    if #servers == 0 then warn("No servers available") return end

    local currentJob = tostring(game.JobId or "")
    local chosenJob
    for _ = 1, #servers do
        local candidate = tostring(servers[math.random(1, #servers)] or "")
        if candidate ~= "" and candidate ~= currentJob then
            chosenJob = candidate
            break
        end
    end
    if not chosenJob then warn("No new server found") return end

    isHopping = true
    warn("Teleporting to server:", chosenJob)
    pcall(function() TeleportService:TeleportToPlaceInstance(game.PlaceId, chosenJob, player) end)
    task.wait(HOP_COOLDOWN)
    isHopping = false
end

-- Main loop
task.spawn(function()
    task.wait(2)
    lastCheck = tick()

    while true do
        local foundRift = false
        local rendered = Workspace:FindFirstChild("Rendered")
        local riftsFolder = rendered and rendered:FindFirstChild("Rifts")
        if riftsFolder then
            local rifts = riftsFolder:GetChildren()
            for _, rift in ipairs(rifts) do
                if alreadyFound[rift] then continue end

                local riftNameNorm = normalize(rift.Name)
                for _, riftData in ipairs(RIFTS) do
                    if not riftData.Name or not riftNameNorm:find(normalize(riftData.Name)) then continue end

                    -- Rift details
                    local luckText, timerText, heightText = "N/A", "N/A", "N/A"
                    local minutes, seconds = 0, 0

                    local display = rift:FindFirstChild("Display")
                    if display then
                        local gui = display:FindFirstChild("SurfaceGui")
                        if gui then
                            local icon = gui:FindFirstChild("Icon")
                            if icon and icon:FindFirstChild("Luck") then
                                luckText = icon.Luck.Text or "N/A"
                            end
                            local timer = gui:FindFirstChild("Timer")
                            if timer then timerText = timer.Text or "" end
                        end
                        if rift.GetPivot then
                            heightText = tostring(math.floor(rift:GetPivot().Position.Y)) .. " studs"
                        elseif display:IsA("BasePart") then
                            heightText = tostring(math.floor(display.Position.Y)) .. " studs"
                        end
                    end

                    -- Parse timer
                    minutes = tonumber(timerText:match("(%d+) ?m")) or 0
                    seconds = tonumber(timerText:match("(%d+) ?s")) or 0
                    if minutes == 0 and seconds == 0 then
                        local mm, ss = timerText:match("(%d+):(%d+)")
                        if mm and ss then
                            minutes, seconds = tonumber(mm) or 0, tonumber(ss) or 0
                        end
                    end

                    if not shouldSendRift(riftData, luckText) then continue end

                    local formattedEggName = formatEggName(rift.Name)
                    local expireTimestamp = os.time() + (minutes * 60 + seconds)
                    local jobId = tostring(game.JobId or "")
                    local playerCount = #Players:GetPlayers()
                    local maxPlayers = Players.MaxPlayers or 0

                    -- Build webhook payload
                    local message = {
                        embeds = {{
                            title = formattedEggName .. " Has Been Found ü•ö",
                            color = 0x00FF00,
                            fields = {
                                {name = "üåç Server Info", value = "Players Online: " .. playerCount .. "/" .. maxPlayers, inline = true},
                                {name = "üé≤ Rift Info", value = "Luck: " .. luckText .. "\nExpires: <t:" .. expireTimestamp .. ":R>\nHeight: " .. heightText, inline = true},
                                {name = "üîó Quick Join", value = "[Click to Join Server](https://www.roblox.com/games/start?placeId=" .. game.PlaceId .. "&jobId=" .. jobId .. ")", inline = false}
                            },
                            timestamp = DateTime.now():ToIsoDate()
                        }}
                    }

                    -- Send webhook
                    sendWebhook(riftData.Webhook, message)
                    print("Webhook sent for:", rift.Name)

                    alreadyFound[rift] = true
                    task.delay(300, function() alreadyFound[rift] = nil end)

                    hopServers()
                    foundRift = true
                end
            end
        end

        if not foundRift and (tick() - lastCheck) >= IDLE_HOP_TIME then
            hopServers()
            lastCheck = tick()
        end

        task.wait(1)
    end
end)
